example/scripts/02-message-on-complete.nf<pre><code>include { slackMessage } from 'plugin/nf-slack'

process HELLO {
    input:
    val sample_id

    output:
    val sample_id

    script:
    """
    echo "Processing ${sample_id}"
    """
}

workflow {
    samples = Channel.of('sample1', 'sample2', 'sample3')
    HELLO(samples)
}

workflow.onComplete {
    def status = workflow.success ? '✅ Success' : '❌ Failed'
    slackMessage([
        message: "*Pipeline ${status}*",
        fields: [
            [title: 'Duration', value: "${workflow.duration}", short: true]
        ]
    ])
}
</code></pre>

example/scripts/03-file-upload.nf<pre><code>include { slackFileUpload } from 'plugin/nf-slack'

process GENERATE_REPORT {
    output:
    path 'qc_summary.csv'

    script:
    """
    echo "sample,reads,quality" &gt; qc_summary.csv
    echo "sample1,1000000,38.5" &gt;&gt; qc_summary.csv
    echo "sample2,1200000,39.1" &gt;&gt; qc_summary.csv
    echo "sample3,950000,37.8" &gt;&gt; qc_summary.csv
    """
}

workflow {
    report = GENERATE_REPORT()

    // Simple upload
    report | map { slackFileUpload(file: it) }

    // Upload with metadata
    report | map {
        slackFileUpload(
            file: it,
            title: 'QC Summary Report',
            comment: 'Automatically generated QC metrics'
        )
    }
}
</code></pre>
